import React from 'react';
import styled from 'styled-components';
import { PageHero } from '../components';
import aboutImg from '../assets/hero-bcg.jpeg';

const AboutPage = () => {
  return (
    <main>
      <PageHero title="about" />
      <Wrapper className="page section section-center">
        {/* <img src={aboutImg} alt="nice desk" /> */}
        <article>
          <div className="title">
            <h2>
              our story
              <div className="underline"></div>
            </h2>
          </div>
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Voluptatem
            delectus, illum quasi veniam possimus aut voluptatum voluptatibus
            esse tenetur numquam cumque dolores quam odio voluptates asperiores
            maiores unde iusto laborum ipsum quod corrupti, pariatur assumenda
            perferendis. Pariatur accusamus ab, recusandae necessitatibus nemo
            nobis sit mollitia rem, natus sed eius reiciendis!
          </p>
        </article>
      </Wrapper>
    </main>
  );
};

const Wrapper = styled.section`
  display: grid;
  gap: 4rem;
  img {
    width: 100%;
    display: block;
    border-radius: var(--radius);
    height: 500px;
    object-fit: cover;
  }
  p {
    line-height: 2;
    max-width: 45em;
    margin: 0 auto;
    margin-top: 2rem;
    color: var(--clr-grey-1);
  }
  .title {
    text-align: left;
  }
  .underline {
    margin-left: 0;
  }
  @media (min-width: 992px) {
    grid-template-columns: 1fr 1fr;
  }
`;
export default AboutPage;
